     console.log()
   })





  return (
    <div className="App">
      <h2>Let's get started!</h2>
      <div className="expense-items-container">
        <Form/>
        {expenses.map((expense, index) => 
          <ExpenseItem 
            key={index}
            title={expense.title} 
            amount={expense.amount} 
            date={expense.date}
          />
        )}
      </div>
    </div>
  );
}

export default App;
